    $(document).ready(function(){
        tampil_data_account();	//pemanggilan fungsi tampil account.
        
        $('#mydata').dataTable();
        
        //fungsi tampil barang
        function tampil_data_account(){
            $.ajax({
                type  : 'ajax',
                url   : '<?= site_url()?>/auth/data_account',
                async : false,
                dataType : 'json',
                success : function(data){
                    var html = '';
                    var i;
                    for(i=0; i<data.length; i++){
                        html += '<tr>'+
                                '<td style="text-align:right;">'+
                                    '<a href="javascript:;" class="btn btn-info btn-xs item_edit" data="'+data[i].id_account+'"><i class="icon-note"></i></a>'+' '+
                                    '<a href="javascript:;" class="btn btn-danger btn-xs item_hapus" data="'+data[i].id_account+'"><i class="icon-ban"></i></a>'+
                                '</td>'+
                                '<td class="text-center">'+data[i].id_account +'</td>'+
                                '<td class="text-center">'+data[i].status+'</td>'+
                                '<td class="text-center">'+data[i].prov+'</td>'+
                                '<td class="text-center">'+data[i].kota_kab+'</td>'+
                                '<td class="text-center"><a href="">'+data[i].satuan_pen+'</a></td>'+
                                '<td class="text-center">'+data[i].nama_op+'</td>'+
                                '<td class="text-center">'+data[i].username+'</td>'+
                                '<td class="text-center">'+data[i].password+'</td>'+
                                '<td class="text-center">'+data[i].tipe+'</td>'+
                                '<td class="text-center">'+data[i].jenis_sek+'</td>'+
                                '<td class="text-center">'+data[i].grub+'</td>'+
                                '<td class="text-center">'+data[i].email+'</td>'+
                                '<td class="text-center">'+data[i].telp+'</td>'+
                                '<td class="text-center">'+data[i].hp+'</td>'+
                                '<td class="text-center">'+data[i].telegramId+'</td>'+
                                '<td class="text-center">'+data[i].created+'</td>'+
                                '<td class="text-center">'+data[i].modified+'</td>'+
                                '</tr>';
                    }
                    $('#show_data').html(html);
                }

            });
        }
        
        //Simpan Account
        $('#btn_simpan').on('click',function(){
            var koId=$('#kode_account').val();
            var acname=$('#account_name').val();
            var acprov=$('#account_prov').val();
            var ackab=$('#account_kab').val();
            var acsek=$('#account_sek').val();
            var acnama=$('#account_nama').val();
            var acuser=$('#account_user').val();
            var acpass=$('#account_pass').val();
            var actipe=$('#account_tipe').val();
            var acjen=$('#account_jen').val();
            var acgrub=$('#account_grub').val();
            var acemail=$('#account_email').val();
            var actelp=$('#account_telp').val();
            var achp=$('#account_hp').val();
            var actelegramId=$('#account_telegramId').val();
            var accreated=$('#account_created').val();
            $.ajax({
                type : "POST",
                url  : "<?php echo base_url('/auth/simpan_account')?>",
                dataType : "JSON",
                data : {koId:koId , acname:acname, acprov:acprov, ackab:ackab, acsek:acsek, acnama:acnama, acuser:acuser, acpass:acpass, actipe:actipe, acjen:acjen, acgrub:acgrub, acemail:acemail, actelp:actelp, achp:achp, actelegramId:actelegramId, accreated:accreated},
                success: function(data){
                    $('[name="koId"]').val("");
                    $('[name="acname"]').val("");
                    $('[name="acprov"]').val("");
                    $('[name="ackab"]').val("");
                    $('[name="acsek"]').val("");
                    $('[name="acnama"]').val("");
                    $('[name="acuser"]').val("");
                    $('[name="acpass"]').val("");
                    $('[name="actipe"]').val("");
                    $('[name="acjen"]').val("");
                    $('[name="acgrub"]').val("");
                    $('[name="acemail"]').val("");
                    $('[name="actelp"]').val("");
                    $('[name="achp"]').val("");
                    $('[name="actelegramId"]').val("");
                    $('[name="accreated"]').val("");
                    $('#ModalaAdd').modal('hide');
                    tampil_data_account();
                }
            });
            return false;
        });
        
        
        
        //GET HAPUS
        $('#show_data').on('click','.item_hapus',function(){
            var id=$(this).attr('data');
            $('#ModalHapus').modal('show');
            $('[name="kode"]').val(id);
        });
        
        //Hapus Barang
        $('#btn_hapus').on('click',function(){
            var kode=$('#textkode').val();
            $.ajax({
            type : "POST",
            url  : "<?php echo base_url('/auth/hapus_account')?>",
            dataType : "JSON",
                data : {kode: kode},
                success: function(data){
                        $('#ModalHapus').modal('hide');
                        tampil_data_account();
                }
            });
            return false;
        });
        
    //GET UPDATE
    $('#show_data').on('click','.item_edit',function(){
            var id=$(this).attr('data');
            $.ajax({
                type : "GET",
                url  : "<?php echo base_url('/auth/get_account')?>",
                dataType : "JSON",
                data : {id:id},
                success: function(data){
                    $.each(data,function(id_account, username, password, email, grub, telp, hp, telegramId, status){
                        $('#ModalaEdit').modal('show');
                        $('[name="akun_edit"]').val(data.id_account);
                        $('[name="user_edit"]').val(data.username);
                        $('[name="pass_edit"]').val(data.password);
                        $('[name="email_edit"]').val(data.email);
                        $('[name="grub_edit"]').val(data.grub);
                        $('[name="telp_edit"]').val(data.telp);
                        $('[name="hp_edit"]').val(data.hp);
                        $('[name="teleg_edit"]').val(data.telegramId);
                        $('[name="status_edit"]').val(data.status);
                    });
                }
            });
            return false;
        });

        
    //Update Barang
    $('#btn_update').on('click',function(){
            var kobar=$('#kode_akun2').val();
            var uname=$('#nama_user2').val();
            var pass=$('#pass2').val();
            var grub=$('#grub2').val();
            var uemail=$('#email2').val();
            var status=$('#status2').val();
            var telp=$('#telp2').val();
            var hp=$('#hp2').val();
            var teleg=$('#teleg2').val();
            var jum=$('#jum2').val();
            var modif=$('#modif2').val();
            $.ajax({
                type : "POST",
                url  : "<?php echo base_url('/auth/update_account')?>",
                dataType : "JSON",
                data : {kobar:kobar , uname:uname, pass:pass, uemail:uemail, grub:grub, status:status, telp:telp, hp:hp, teleg:teleg, jum:jum, modif:modif},
                success: function(data){
                    $('[name="kobar_edit"]').val("");
                    $('[name="nabar_edit"]').val("");
                    $('[name="pass_edit"]').val("");
                    $('[name="email_edit"]').val("");
                    $('[name="grub_edit"]').val("");
                    $('[name="status_edit"]').val("");
                    $('[name="telp_edit"]').val("");
                    $('[name="hp_edit"]').val("");
                    $('[name="teleg_edit"]').val("");
                    $('[name="jum_edit"]').val("");
                    $('[name="modif_edit"]').val("");
                    $('#ModalaEdit').modal('hide');
                    tampil_data_account();
                }
            });
            return false;
        });
        
        
    });